custom.scss:
// PASTE CUSTOM STYLING HERE
.price-visibility > span, .productView-price span{ 
    color: #ef7b47;
} 
.header-section-search
{ display:none;}

@media screen and (max-width: 767px) {
    .close.agree {
        position: absolute;
        top: 20px;
        right: 60px;
        width: 15px;
        height: 15px;
        display: flex;
        align-items: center;
        justify-content: center;
    }
    
    #newsletter-popup #dialog {
        width: 100%;
        height: auto;
        margin: 0 auto !important;
        left: 0 !important;
        top: 20% !important;
    }
}

.header-logo-image {
    display: initial;
}

.categorie-title{
    font-family: 'Roboto Condensed';
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 16px;
    /* identical to box height, or 100% */

    text-transform: uppercase;

    color: #1E3E5D;
    @media screen and (min-width: 767px){
        display: none;
    }
}

.categories-carousel{
    margin-bottom: 4rem;
    @media screen and (min-width: 767px){
        display: none !important;
    }
    a {
        text-decoration: none;
        font-family: 'Roboto Condensed';
        font-style: normal;
        font-weight: 400;
        font-size: 14px;
        line-height: 16px;
        /* identical to box height, or 114% */


        color: #000000;
    }

    .slick-slide{
        margin-right: 5px;
    }
}

.form-body {
    background-color: #ffffff !important;
}

.top-ribbon{
    position: fixed;
    top: 0;
    width: 100%;
    height: 56px;
    z-index: 45;

    background: #F0F0F0;

    @media screen and (max-width: 767px){
        height: 88px;
    }
    .content-ribbon {
        max-width: 1440px !important;
        margin: 0 auto;
        text-align: center;
        position: relative;
        top: 25%;
        @media screen and (max-width: 767px){
            height: 88px;
            top: 0;
            display: flex;
            justify-content: center;
            flex-direction: column;
            align-items: center;
        }
    }

    span {
        font-family: 'Roboto Condensed';
        font-style: normal;
        font-weight: 400;
        font-size: 16px;
        line-height: 19px;
        /* identical to box height */
        

        color: #1E3E5D;
    }

    li{
        font-family: 'Roboto Condensed';
        font-style: normal;
        font-weight: 700;
        font-size: 16px;
        line-height: 19px;
        /* identical to box height */


        color: #3A3A3A;
    }

    .fa-shopping-cart{
        margin-right: 0 !important;
        background: none !important;
        font-size: 25px !important;
        border-bottom: 0 !important;
        border-color: transparent !important;
        color: #E47B47 !important;
    }

    .alm{
        @media screen and (max-width: 767px){
            display: block !important;
        }
    }

    .ald{
        // @media screen and (max-width: 767px){
        //     display: none !important;
        // }
        .navUser-item--account{
            @media screen and (max-width: 767px){
                display: none;
            }
        }
    }

    .or-text{
        @media screen and (max-width: 767px){
            display: none !important;
        }
    }

    .ddmm{
        
        @media screen and (max-width: 767px){
            left: -301px !important;
            top: 59px !important;
            width: 358px !important;
        }
    }
}
